import { server as serverConfig, origins } from '../config'
import { devStream } from './stream'

const logOriginPass = (origin, pattern) => log.trace(`PASS: origin [${origin}] matches pattern [${pattern}].`)
const logOriginFail = (origin) => log.trace(`FAIL: origin [${origin}] does not match any patterns.`)
const logOriginDNE = () => log.trace('DNE: origin header does not exist.')

const corsValidator = patterns => {
  return req => (typeof req.headers.origin === 'string'
                ? patterns.filter(x => x.test(req.headers.origin))
                          .map(devStream(x => logOriginPass(req.headers.origin, x))).length > 0
                : true
                )
}

export function getCors() {
  const isOriginOk = origins ? corsValidator(origins.map(x => new RegExp(x))) : true

  const failureStatus = { code: 403
                        , message: '403 Forbidden'
                        }

  const getError = req => req.headers.origin  ? ({ message: logOriginFail(req.headers.origin), code: 10 })
                                              : ({ message: logOriginDNE(), code: 11 })

  const getOptionsHeaders = req => ({ 'Access-Control-Allow-Origin': req.headers.origin
                                    , 'Access-Control-Max-Age': 604800 // Specifies how long the preflight response can be cached in seconds
                                    , 'Access-Control-Allow-Methods': 'GET, PUT, POST, DELETE'
                                    , 'Access-Control-Allow-Headers': 'Content-Type, Authorization'
                                    , 'Access-Control-Allow-Credentials': true
                                    })

  const handle = (req, res) => {
    const resolvedOrigin = req.headers.origin || req.headers.host
    if(!isOriginOk(req))
      return req.headers.origin ? log.error(`proxy -origin [${resolvedOrigin}], method [${req.method}]`) : log.warn('proxy -host [%s], method [%s]', req.headers.host, req.method)
    log.trace(`proxy +${req.headers.origin ? 'origin' : 'host'} [${resolvedOrigin}], method [${req.method}]`)
    res.setHeader('Access-Control-Allow-Origin', resolvedOrigin)
    res.setHeader('Access-Control-Allow-Credentials', true)
  }

  const handlePreflight = (req, res) => {
    res.writeHead(200, getOptionsHeaders(req))
    res.end()
  }

  const handleFailure = (req, res) => {
    res.writeHead(failureStatus.code)
    res.end (JSON.stringify({ message: failureStatus.message
                            , errors: [ getError(req) ]
                            }))
  }

  return  { isOriginOk : isOriginOk
          , failureStatus: failureStatus
          , getError: getError
          , getOptionsHeaders: getOptionsHeaders
          , handle: handle
          , handlePreflight: handlePreflight
          , handleFailure: handleFailure
          }
}

import ExtendableError from './ExtendableError'

export default class DOMError extends ExtendableError {
  constructor(message, innerError) {
    super(message, innerError)
    this.name = 'DOMError'
  }
}

export default class ExtendableError extends Error {
  constructor(message, innerError) {
    super(message)
    this.name = this.constructor.name
    this.message = message
    this.innerError = innerError
    if(Error.captureStackTrace)
      Error.captureStackTrace(this, this.constructor.name)
    else
      this.stack = (new Error()).stack
  }
}

import ExtendableError from './ExtendableError'

export default class IdentityError extends ExtendableError {
  constructor(message, innerError, props = {}) {
    super(message, innerError)
    this.name = 'IdentityError'
    this.message = message
    this.innerError = innerError
    const { tokens, fingerprint } = props
    this.tokens = tokens
    this.fingerprint = fingerprint
  }
}

import ExtendableError from './ExtendableError'

export const zeroElementsStateError = (prop, ...args) => new StateError(`Cannot remove from state property '${prop}' with zero elements.`, ...args)

export default class StateError extends ExtendableError {
  constructor(message, innerError) {
    super(message, innerError)
    this.name = 'StateError'
  }
}

import Promise from 'bluebird'
import { assert } from 'chai'
import path from 'path'

import configureServer from './server'
import { server, __rootname, resolveRoot } from '../config'

const mkdirp = Promise.promisify(require('mkdirp'))

/** Exports a server map of key (schemes) to server startup functions. */
export default paths => configureServer({ paths })
/** A function to create paths that are used throughout the server. */
export const definePaths = ({ NODE_ROOT = __rootname
                            , NODE_MODULES_ROOT = resolveRoot('node_modules')
                            , SERVER_CONFIG_PATH = resolveRoot('config-server.json')
                            , CLIENT_CONFIG_PATH = resolveRoot('config-client.json')
                            , PUBLIC_ROOT = resolveRoot('public')
                            , ASSETS_ROOT = resolveRoot('public/assets')
                            , STATIC_ROOT = resolveRoot('public/static')
                            , IMAGES_ROOT = resolveRoot('public/static/images')
                            , SRC_ROOT = resolveRoot('src')
                            , APP_ROOT = resolveRoot('app')
                            , LIB_ROOT = resolveRoot('lib')
                            , BIN_ROOT = resolveRoot('bin')
                            , LOG_ROOT = resolveRoot('log')
                            , DOC_ROOT = resolveRoot('doc')
                            , MOD_ROOT = path.resolve(server.fs.MOD_ROOT)
                            } = {}) => {
  const paths = { NODE_ROOT, NODE_MODULES_ROOT, SERVER_CONFIG_PATH, CLIENT_CONFIG_PATH, PUBLIC_ROOT, ASSETS_ROOT, STATIC_ROOT, IMAGES_ROOT, SRC_ROOT, APP_ROOT, LIB_ROOT, DOC_ROOT, BIN_ROOT, LOG_ROOT, MOD_ROOT }


  Object.keys(paths).forEach(x => assert.typeOf(paths[x], 'string', `path ${x} must be a string`) && assert.isAbove(paths[x].length, 0, `path ${x} must have length greater than 0`))
  return mkdirp(paths.LOG_ROOT)
    .then(() => paths)
    .catch(err => {
      console.error('An error occurred attempting to create log directory.', err)
      process.exit(1)
    })
}

/***
 * Proxy Module
 * Responsible for reading proxy config and setting up proxies.
 * @module server/proxy
 */
import httpProxy from 'http-proxy'
import http from 'http'
import https from 'https'
import { server as serverConfig, log } from '../config'

const proxyListen = ({ name, fromPort, toHost, toPort }) => `proxy ${name} directing from :${fromPort} to ${toHost}:${toPort}`

export default function proxy() {
  const cdnBindings = new Map(serverConfig.bindings.cdn)
  const hasHttp = cdnBindings.has('http')
  const hasHttps = cdnBindings.has('https')
  //const cors = getCors()
  const proxyConfigs = Array.isArray(serverConfig.proxies) ? serverConfig.proxies : [ serverConfig.proxies ]
  if(proxyConfigs.length === 0)
    return

  /*** Iterate proxy configs and start routing */
  proxyConfigs.forEach(config => {
    const options = getProxyOptions(config)
    const proxyServer = httpProxy.createProxyServer(options)
    const listenMessage = proxyListen(config)

    proxyServer.on('error', (err, req, res) => {
      log.error(err, 'error proxying request')
      res.end()
    })

    proxyServer.on('proxyReq', (proxyReq, req, res, options) => { /* This event fires on proxy requests */ })


    createProxyServer(http.createServer(onProxy))

    const createProxyServer = server => {
      if (config.allowWebSockets)
        server.on('upgrade', (req, socket, head) => server.ws(req, socket, head))
      server.listen(config.fromPort, () => log.info(listenMessage))
    }

    const onProxy = (req, res) => {
      //if(!cors.isOk(req))
        //return cors.handleFailure(req, res)

      if(config.stub && config.stub[req.method])
        return proxyStub(req, res)

      if(req.method === 'OPTIONS')
        //return cors.handlePreflight(req, res)

      if (config.latency)
        setTimeout(() => proxyServer.web(req, res), config.latency)
      else
        proxyServer.web(req, res)
    }

    const proxyStub = (req, res) => {
      var stub = config.stub[req.method]
      res.writeHead(200, stub.headers)
      if (stub.headers['content-length'] === 0) res.end()
      else res.end(JSON.stringify(stub.body))
    }
  })
}

const getProxyOptions = config => {
  var opts =  { target: config.target || `${config.toScheme}://${config.toHost}:config.toPort`
              , xfwd: true
              }
  opts.secure = config.secure
  return opts
}

import Promise from 'bluebird'
import 'isomorphic-fetch'
import { createAction } from 'redux-actions'
import jwtSimple from 'jwt-simple'

import { client, log } from '../../../config'
import { scheme, hostname, port, host, path } from '../../services/location'
import persistence from '../../services/persistence'
import  { CLEAR_DATA
        , FETCH_DATA
        , RECEIVE_DATA
        , KEYED_DATA
        } from '../constants'

import { forgetTokens, refreshIdentity } from './identity'
import APIError from '../../errors/APIError'

const { saveState, loadState, removeState } = persistence()
const getPersisted = () => loadState(['tokens', 'fingerprint'])

const { apis, clientId } = client

const noop = () => {}

export const clearData = createAction(CLEAR_DATA, ([apiName, actionName]) => ({ apiName, actionName }))
const fetchData = createAction(FETCH_DATA, ([apiName, actionName], inputData) => ({ apiName, actionName, inputData }))
const receiveData = createAction(RECEIVE_DATA, ([apiName, actionName], data) => ({ apiName, actionName, data }))
const receiveDataError = createAction(RECEIVE_DATA)
const keyedData = createAction(KEYED_DATA, ([apiName, actionName], [apiKey, actionKey], data) => ({ apiName, actionName, apiKey, actionKey, data }))
const keyedDataError = createAction(KEYED_DATA)


export const decodeToken = accessToken => jwtSimple.decode(accessToken, null, true)
export const isExpired = decodedToken => getExpiresInMS(decodedToken) < 0
const getExpiresInMS = decodedToken => decodedToken.exp * 1000 - Date.now()

const getTixClaimSchema = name => `http://schemas.tix.com/identity/claims/${name}`
const getClaim = (decodedToken, name) => decodedToken[name]
const hasTixClaim = (decodedToken, name, value) => hasClaim(decodedToken, getTixClaimSchema(name), value)
const getTixClaim = (decodedToken, name) => getClaim(decodedToken, getTixClaimSchema(name))
const hasRole = (decodedToken, roleName) => hasClaim(decodedToken, getTixClaimSchema('role'), roleName)
const hasClaim = (decodedToken, name, value) => {
  let claim = getClaim(decodedToken, name)
  // if claim exists but value was not specified, return true
  if (claim && !value)
    return true
  if (claim)
    return Array.isArray(claim) ? claim.indexOf(value) !== -1 : claim === value
}

const resolveScheme = actionConfig => scheme
const resolvePort = actionConfig => actionConfig.extra && actionConfig.extra.port ? actionConfig.extra.port : port
const resolveHostname = actionConfig => actionConfig.extra && actionConfig.extra.hostname ? actionConfig.extra.hostname : hostname

const resolveBaseUrl = actionConfig => {
  let resolvedScheme = resolveScheme(actionConfig)
  let resolvedPort = resolvePort(actionConfig)
  let resolvedHostname = resolveHostname(actionConfig)
  if((resolvedScheme === 'https' && resolvedPort === 443) || (resolvedScheme === 'http' && resolvedPort === 80)) { /** NO NEED TO SPECIFY PORT IN HERE */
    if(resolvedScheme === scheme && resolvedHostname === hostname && resolvedPort === port) /** SAME HOSTNAME, PORT, and SCHEME, NO NEED TO SPECIFY BASE URL */
      return ''
    return `${resolvedScheme}://${resolvedHostname}`
  }
  return `${resolvedScheme}://${resolvedHostname}:${resolvedPort}`
}

const supportedPropTypes = ['string', 'bool', 'number']
const checkPropType = (key, prop, propType) => {
  if(typeof prop === 'undefined' || prop === null)
    throw new APIError(`Parameter with key '${key}' was null but API was expecting propType '${propType}'.`)
  const getTypeError = () => new APIError(`Parameter with key '${key}' was not of expected propType '${propType}'.`)
  switch(propType) {
    case 'string':
      if (typeof prop !== 'string')
        throw getTypeError()
      break
    case 'bool':
      if(typeof prop !== 'boolean')
        throw getTypeError()
      break
    case 'number':
      if(typeof prop !== 'number')
        throw getTypeError()
      break
    case 'object':
      if(typeof prop !== 'object')
        throw getTypeError()
      break
    case 'array':
      if(!Array.isArray(prop))
        throw getTypeError()
      break
    case 'func':
    default:
      throw new APIError(`propType ${propType} is not supported for api interpolation. Reconfigure the api to use one of the supported propTypes => [${supportedPropTypes.join(', ')}]`)
  }
}


const resolveQuery = (actionConfig, inputData) => {
  if(!(actionConfig.extra && actionConfig.extra.expects && actionConfig.extra.expects.query))
    return ''
  // TODO: COMPILE QUERY STRING
  return '?'
}

const getTemplateRegExp = replacer => new RegExp(`\\$\\{${replacer}\\}`, 'gi')
/**
 * PATH = /some/path/looks/like/this/${variableName}/${another}
 * replacers = { variableName: 'path_seg_one', another: 'path_seg_two' }
 */
const compileTemplate = (path, replacers) => {
  if(path.includes(' '))
    throw new APIError('Space detected in path. This is not allowed.')
  for(let key of Object.keys(replacers)) {
    let re = getTemplateRegExp(key)
    path = path.replace(re, replacers[key])
  }
  return path
}

const resolvePath = (actionConfig, inputData) => {
  // TODO: NEED TO COMPILE DYNAMIC PATHS
  if(!(actionConfig.extra && actionConfig.extra.expects && actionConfig.extra.expects.route))
    return actionConfig.path

  const { route } = actionConfig.extra.expects
  let possibleVariables = Object.keys(route)
  let matchedVariables = Object.keys(inputData).filter(x => typeof inputData[x] !== 'undefined' && possibleVariables.includes(x))

  if(__DEV__) {
    //** VALIDATE REQUIRED VARIABLES */
    let requiredVariables = possibleVariables.filter(x => route[x].isRequired)
    let missedVariables = requiredVariables.filter(x => matchedVariables.includes(x) === false)
    if(missedVariables.length > 0)
      throw new APIError(`Route with path ${actionConfig.path} requires the following missing route variables => [${missedVariables.join(', ')}]`)

    //** VALIDATE TYPED VARIABLES */
    let typedVariables = matchedVariables.filter(x => route[x].propType)
    typedVariables.forEach(x => checkPropType(x, inputData[x], route[x].propType))
  }

  /** Reduce to an object with key value map of things to be replaced. */
  let replacers = matchedVariables.reduce((obj, key) => Object.assign({}, obj, { [key]: inputData[key].toString() }), {})
  return compileTemplate(actionConfig.path, replacers)
}

const resolveBody = (actionConfig, inputData) => {
  if(!(actionConfig.extra && actionConfig.extra.expects && actionConfig.extra.expects.body))
    return noop()

  const { body } = actionConfig.extra.expects
  let possibleVariables = Object.keys(body)
  let matchedVariables = Object.keys(inputData).filter(x => typeof inputData[x] !== 'undefined' && possibleVariables.includes(x))

  if(__DEV__) {
    //** VALIDATE REQUIRED VARIABLES */
    let requiredVariables = possibleVariables.filter(x => body[x].isRequired)
    let missedVariables = requiredVariables.filter(x => matchedVariables.includes(x) === false)
    if(missedVariables.length > 0)
      throw new APIError(`Route with path ${actionConfig.path} requires the following missing body variables => [${missedVariables.join(', ')}], passed => [${JSON.stringify(inputData)}]`)

    //** VALIDATE TYPED VARIABLES */
    let typedVariables = matchedVariables.filter(x => body[x].propType)
    typedVariables.forEach(x => checkPropType(x, inputData[x], body[x].propType))
  }
  let resolvedBody = matchedVariables.reduce((obj, key) => Object.assign({}, obj, { [key]: inputData[key] }), {})
  return JSON.stringify(resolvedBody)
}

const resolveInput = (actionConfig, inputData) => `${resolveBaseUrl(actionConfig)}${resolvePath(actionConfig, inputData)}${resolveQuery(actionConfig, inputData)}`

const resolveInit = (actionConfig, inputData) => {
  /** TODO: NEED TO COMPILE ROUTES PATHS AND TEST IF THERE ARE REQUIRED DATA */
  let body = resolveBody(actionConfig, inputData)
  return Object.assign({}, actionConfig.init, { body })
}

const getErrorCode = response => {
  switch(response.statusText) {
    default:
      return 'API_UNCLASSIFIED'
  }
}

const getAPIError = (info, response) => {
  const code = getErrorCode(response)
  switch(response.status) {
    default:
      return new APIError(`An API error occurred => ${response.statusText}`, null, { ...info, response, code })
  }
}

let activeRequests = new Map()
function executeFetch (input, init, transform, { apiName, actionName, inputData }) {
  return (dispatch, getState) => {
    const info = { input, init, apiName, actionName, inputData }
    const key = JSON.stringify({ ...info, transform })
    let request = activeRequests.get(key)
    if(!request) {
      dispatch(fetchData([apiName, actionName], inputData))
      request = fetch(input, init)
                  .then(response => {
                    if(!response.ok) {
                      if(response.status === 401)
                        dispatch(forgetTokens())
                      throw getAPIError(info, response)
                    }
                    return response.json()
                  })
                  .then(json => {
                    let data = json
                    if(transform)
                      data = Array.isArray(json) ? json.map(transform) : transform(json)
                    activeRequests.delete(key)
                    dispatch(receiveData([apiName, actionName], data))
                    return data
                  })
                  .catch(err => {
                    activeRequests.delete(key)
                    dispatch(receiveDataError(err))
                    throw err
                  })
      activeRequests.set(key, request)
    } else
      log.info({ apiName, actionName }, 'request already processing, continuing on enqueued response.')
    return request
  }
}


const resolveInputData = args => {
  if(args.length > 0 && typeof args[0] !== 'function')
    return args[0]
}

const resolveTransform = args => {
  if(args.length > 0) {
    let lastArg = args[args.length - 1]
    if(typeof lastArg === 'function')
      return lastArg
  }
}

const getDecodedToken = accessToken => jwtSimple.decode(accessToken, null, true)

const getCookieData = () => {
  const { tokens } = getPersisted()
  const [accessToken, refreshToken] = tokens || []
  const decodedToken = getDecodedToken(accessToken)
  const fingerprint = getTixClaim(decodedToken, 'fingerprint')
  const subject = getClaim(decodedToken, 'sub')
  return { accessToken, refreshToken, fingerprint, subject }
}

const authorizeInit = init => {
  const { accessToken } = getCookieData()
  let headers = Object.assign({}, init.headers, { Authorization: `Bearer ${accessToken}`})
  return Object.assign({}, init, { headers })
}


export function apiAction([apiName, actionName], ...args) {
  const inputData = resolveInputData(args)
  const transform = resolveTransform(args)
  return (dispatch, getState) => {
    try {
      let apiConfig = apis[apiName]
      if(!apiConfig) {
        let err = new APIError(`Specified api does not exist, ensure api '${apiName}' exists in api config.`, null, { apiName, actionName, inputData })
        dispatch(receiveDataError(err))
        return Promise.reject(err)
      }
      let actionConfig = apiConfig[actionName]
      if(!actionConfig) {
        let err = new APIError(`Specified api action does not exist, ensure api '${apiName}' has an action definition for ${actionName}`, null, { apiName, actionName, inputData })
        dispatch(receiveDataError(err))
        return Promise.reject(err)
      }

      let info = { apiName, actionName, inputData }
      let input = resolveInput(actionConfig, inputData)
      let init = resolveInit(actionConfig, inputData)
      const isAuthorized = actionConfig.extra && actionConfig.extra.isAuthorized
      return dispatch(executeFetch(input, isAuthorized ? authorizeInit(init) : init, transform, info))
    } catch(innerError) {
      let err = new APIError('Unexpected error occurred during data fetch.', innerError, { apiName, actionName })
      dispatch(receiveDataError(err))
      return Promise.reject(err)
    }
  }
}

export function keyedApiAction([apiName, actionName], [apiKey, actionKey], ...args) {
  return (dispatch, getState) => {
    try {
      dispatch(apiAction([apiName, actionName], ...args))
        .then(data => {
          dispatch(keyedData([apiName, actionName], [apiKey, actionKey], data))
          return data
        })
    } catch(innerError) {
      let err = new APIError('Unexpected error occurred during keyed api fetch.', innerError, { apiName, actionName, actionKey })
      dispatch(keyedDataError(err))
    }
  }
}


export const logAccess = (path, query, title) => dispatch => dispatch(apiAction(['log', 'access'], { path, query, title }))

import { createAction } from 'redux-actions'
import { apiAction } from './api'
import  { UPDATE_INPUT_VALUE
        , CLEAR_SUGGESTIONS
        , FETCH_SUGGESTIONS
        , FILTER_SUGGESTIONS
        } from '../constants'

export const updateInputValue = createAction(UPDATE_INPUT_VALUE, ([apiName, actionName], value) => ({ apiName, actionName, value }))
export const clearSuggestions = createAction(CLEAR_SUGGESTIONS, ([apiName, actionName]) => ({ apiName, actionName }))
export const fetchSuggestions = createAction(FETCH_SUGGESTIONS, ([apiName, actionName]) => ({ apiName, actionName }))
export const filterSuggestions = createAction(FILTER_SUGGESTIONS, ([apiName, actionName], suggestions, value) => ({ apiName, actionName, suggestions, value }))
export const filterSuggestionsError = createAction(FILTER_SUGGESTIONS)

export function loadSuggestions([apiName, actionName], value) {
  return (dispatch, getState) => {
    let { api } = getState()
    let suggestions = api.entities.getIn([apiName, actionName])
    if(suggestions)
      return Promise.resolve(dispatch(filterSuggestions([apiName, actionName], suggestions, value)))
    dispatch(fetchSuggestions([apiName, actionName]))
    return dispatch(apiAction([apiName, actionName]))
      .then(data => dispatch(filterSuggestions([apiName, actionName], data, value)))
      .catch(err => dispatch(filterSuggestionsError(err)))
  }
}

import { createAction } from 'redux-actions'
import  { ADD_ERROR
        , DISMISS_ERROR
        , CLEAR_ERRORS
        } from '../constants'

/** TODO: DECIDE BETWEEN THIS STYLE GENERIC OR ERRORS CROSS CUTTING EVENTS FROM ALL CATEGORIES */
//export const addError = createAction(ADD_ERROR, (err, category, event) => ({ err, category, event }))
export const dismissError = createAction(DISMISS_ERROR, (category, id) => ({ category, id }))
export const clearErrors = createAction(CLEAR_ERRORS)

import { assert } from 'chai'
import { createAction } from 'redux-actions'
import { push } from 'react-router-redux'
import jwtSimple from 'jwt-simple'
import  { FORGET_TOKENS
        , FORGOTTEN_TOKENS
        , FORGET_FINGERPRINT
        , FORGOTTEN_FINGERPRINT
        , FETCH_IDENTITY
        , SET_IDENTITY
        , PERSISTED_TOKENS
        , PERSISTED_FINGERPRINT
        , POST_AUTHORIZE
        , POST_AUTHORIZE_ADMIN
        , IDENTITY_INVALID
        , IDENTITY_EXPIRED
        , RECEIVE_COOKIES
        } from '../constants'

import { client, log, noop, IS_DEV } from '../../../config'
import { apiAction } from './api'
import IdentityError from '../../errors/IdentityError'
import persistence from '../../services/persistence'
import { goToPath } from '../../services/location'
import { getClaim, getTixClaim } from '../../services/claims'

const { saveState, loadState, removeState } = persistence()
const getPersisted = () => loadState(['tokens', 'fingerprint'])


const { clientId } = client

export const fetchIdentity = createAction(FETCH_IDENTITY)
export const setIdentity = createAction(SET_IDENTITY)
export const forgetTokens = createAction(FORGET_TOKENS)

export const authorized = (accessToken, refreshToken, decodedToken) => (dispatch, getState) => {
  const state = getState()
  if(!state.identity.isAuthorized || (state.identity.tokens.access !== accessToken && state.identity.tokens.refresh !== refreshToken)) {
    dispatch(setIdentity({ tokens: { access: accessToken, refresh: refreshToken }, decodedToken }))
  }
}

const getDecodedToken = accessToken => jwtSimple.decode(accessToken, null, true)

const getCookieData = () => {
  const { tokens, fingerprint } = getPersisted()
  const [accessToken, refreshToken] = tokens || []
  //TODO: THIS MAY HAVE ISSUES IF FINGERPRINT ISNT GETTING SYNCED QUICKLY
  //const decodedToken = getDecodedToken(accessToken)
  //const fingerprint = getTixClaim(decodedToken, 'fingerprint')
  return  { accessToken
          , refreshToken
          , fingerprint
          , subject: accessToken ? getClaim(getDecodedToken(accessToken), 'sub') : noop()
          }
}

const identityApi = (apiPath, args, {  genericError, successRedirect } = {}) => {
  if(IS_DEV) {
    assert.ok(apiPath, 'apiPath is required')
    assert.isArray(apiPath)
    assert.lengthOf(apiPath, 2)
    assert.ok(args, 'args are required')
    assert.typeOf(args, 'object')
  }
  return (dispatch, getState) => {
    return dispatch(apiAction(apiPath, args))
      .then(response => {
        if(response.error)
          throw new IdentityError(`Unable to authorize => ${JSON.stringify(response)}`)
        if(typeof response === 'string' && response.includes('AllowOneTime')) {
          const code = response.split('|')[1]
          return dispatch(push(`/identity/maintenance/migrate/${code}`))
        }
        if(successRedirect) {
          let newState = getState()
          if(newState.identity.isAuthorized) {
            if(typeof successRedirect === 'function') {
              const redirectPath = successRedirect(newState)
              if(typeof redirectPath === 'string')
                goToPath(redirectPath, true)
            } else if(typeof successRedirect === 'string')
              goToPath(successRedirect, true)
          }
        }
      })
      .catch(err => {
        if(genericError)
          dispatch(setIdentity(new IdentityError(typeof genericError === 'function' ? genericError(err) : genericError, err)))
      })
  }
}

export const authorizeIdentity = ({username, password, rememberMe = false}, successRedirect) => {
  if(IS_DEV) {
    assert.ok(username, 'username is required')
    assert.ok(password, 'password is required')
  }
  return (dispatch, getState) => {
    const { fingerprint } = getCookieData()
    const args = { username, password, rememberMe, clientId, fingerprint }
    const continuation = { successRedirect, genericError: 'Username and password were incorrect.' }
    return dispatch(identityApi(['identity', 'authorize'], args, continuation))
  }
}


export const refreshIdentity = () => {
  return (dispatch, getState) => {
    const { accessToken, refreshToken, fingerprint, subject } = getCookieData()
    if(IS_DEV) assert.ok(refreshToken, 'refreshToken is required')
    const args =  { refreshToken, fingerprint, subject, clientId }
    const continuation = { genericError: 'Error occurred while refreshing identity.' }
    return dispatch(identityApi(['identity', 'refresh'], args, continuation))
  }
}

export const impersonateIdentity = ({ organizationId, userId }) => {
  if(IS_DEV) assert.ok(organizationId || userId, 'must specify organizationId or userId to impersonate')
  return (dispatch, getState) => {
    const { accessToken, refreshToken, fingerprint, subject } = getCookieData()
    if(IS_DEV) {
      assert.ok(refreshToken, 'refreshToken is required for impersonate')
      assert.ok(subject, 'subject is required for impersonate')
    }
    const args = { organizationId, userId, fingerprint, subject, refreshToken, clientId }
    const continuation = { genericError: 'Error occurred while impersonating identity.' }
    return dispatch(identityApi(['identity', 'impersonate'], args, continuation))
  }
}


/** Creates an action that auto refreshes JWT tokens using a refresh token and randomized jitter offset time. */
export const createAutoRefresh = ({ getPersisted, jitterMaxMS = 30000 }) => (dispatch, getState) => {
  let { identity } = getState()
  if(!identity.refreshAtMS)
    return (dispatch, getState) => {}
  const jitterMS = Math.floor(Math.random() * jitterMaxMS)
  const refreshMS = (identity.refreshAtMS - jitterMS) - Date.now()

  log.info(`AUTOREFRESH IN ${refreshMS} MS, (JITTER => ${jitterMS})`)
  if(refreshMS < 0) {
    dispatch(refreshIdentity())
    return (dispatch, getState) => {}
  }
  const timeoutID = setTimeout(() => dispatch(refreshIdentity()), refreshMS)
  return (dispatch, getState) => {
    clearTimeout(timeoutID)
    log.debug('AUTOREFRESH CANCELLED')
  }
}

/** Creates an action that synchronizes authorization information across multiple tabs. */
export const createAuthSync = ({ getPersisted, tokenResolver, pollFrequency = 1000 }) => (dispatch, getState) => {
  let intervalID = setInterval(() => {
    const { identity } = getState()
    const stateTokens = identity.tokens

    const cookieTokens = tokenResolver()
    if(!stateTokens) { /** TAB IS NOT AUTHORIZED */
      if(cookieTokens[0]) { /** ANOTHER TAB IS LOGGED IN */
        log.warn({ cookieTokens }, 'DETECTED OTHER TAB AUTHORIZE... SYNCING')
        dispatch(authorized(...cookieTokens))
      }
    } else { // TAB IS AUTHORIZED
      if(!cookieTokens[0] || !cookieTokens[1]) { /** ANOTHER TAB LOGGED OUT */
        log.warn('DETECTED OTHER TAB LOGOUT... FORGETTING')
        dispatch(forgetTokens())
      } else {
        const latest = tokenResolver([stateTokens.access, stateTokens.refresh])
        if(stateTokens.access !== latest[0] || stateTokens.refresh !== latest[1]) {
          log.warn('DETECTED OTHER TAB TOKEN CHANGE WITH NEWER TOKEN... SYNCING')
          dispatch(authorized(...latest))
        }
      }
    }
  }, pollFrequency)
  return () => clearInterval(intervalID)
}

