        , RECEIVE_AUTHORIZE_IDENTITY
        , RECEIVE_REFRESH_IDENTITY
        , RECEIVE_IMPERSONATE_IDENTITY
        } from '../constants'

function entities(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  const { apiName, actionName, actionKey, data } = payload
  switch(type) {
    case KEYED_DATA:
      return state.setIn([apiName, actionKey], data)
    case RECEIVE_DATA:
      return state.setIn([apiName, actionName], data)
    case CLEAR_DATA:
      return state.deleteIn([apiName, actionName])
  }
  return state
}

function isFetching(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(!type || !payload)
    return state
  const { apiName, actionName, actionKey, inputData, data } = payload
  switch(type) {
    case FETCH_DATA:
      return state.setIn([apiName, actionName], !error)
    case RECEIVE_DATA:
    case CLEAR_DATA:
      return state.setIn([apiName, actionName], false)
    case KEYED_DATA:
      return state.setIn([apiName, actionKey], false)
  }
  return state
}

export const initialState = { entities: entities()
                            , isFetching: isFetching()
                            }

export default function api(state = initialState, action) {
  const { type, payload, error } = action
  switch(type) {
    case FETCH_DATA:
      return Object.assign({}, state, { isFetching: isFetching(state.isFetching, action) })
    case RECEIVE_DATA:
    case KEYED_DATA:
    case CLEAR_DATA:
      return Object.assign({}, state, { entities: entities(state.entities, action)
                                      , isFetching: isFetching(state.isFetching, action)
                                      })
  }
  return state
}

import  { UPDATE_INPUT_VALUE
        , CLEAR_SUGGESTIONS
        , FETCH_SUGGESTIONS
        , FILTER_SUGGESTIONS
        } from '../constants'


const digitsRE = /\(?\d+\)?/g
const wordsRE = /[\w\(\)]+/ig

const idFilter = (id, value) => {
  let inputDigits = value.match(digitsRE)
  if(!inputDigits)
    return false
  return inputDigits.some(x => `(${id})`.includes(x))
}

const nameFilter = (name, value) => {
  let inputWords = value.match(wordsRE)
  if(!inputWords)
    return false
  return inputWords.some(x => name.toLowerCase().includes(x.toLowerCase()))
}

const monikerFilter = (moniker, value) => {
  if(!value)
    return false
  return nameFilter(moniker.name, value) || idFilter(moniker.id, value)
}

function value(state = '', action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case UPDATE_INPUT_VALUE:
      const { value } = payload
      return value
  }
  return state
}

function suggestions(state = [], action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case CLEAR_SUGGESTIONS:
      return []
    case FILTER_SUGGESTIONS:
      if(state.value === payload.value)
        return payload.suggestions
      return payload.suggestions.filter(moniker => monikerFilter(moniker, payload.value)).sort((a, b) => (a.id - b.id)).slice(0, 12)
  }
  return state
}

function isLoading(state = false, action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case FETCH_SUGGESTIONS:
      return true
    case FILTER_SUGGESTIONS:
      return false
  }
  return state
}

const initialState =  { value: value()
                      , suggestions: suggestions()
                      , isLoading: isLoading()
                      }

export default function autocomplete(state = initialState, action) {
  const { type, payload, error } = action
  switch (type) {
    case UPDATE_INPUT_VALUE:
    case CLEAR_SUGGESTIONS:
    case FETCH_SUGGESTIONS:
    case FILTER_SUGGESTIONS:
      return Object.assign({}, state, { value: value(state.value, action)
                                      , suggestions: suggestions(state.suggestions, action)
                                      , isLoading: isLoading(state.isLoading, action)
                                      })
  }
  return state
}

